filename = 'hw1CD.dat'[X, Y]=textread(filename,'%f %f','HeaderLines',1)n = length(X);matrix = zeros(n-2, n-2);% calculating part% set up matrixfor i = 2 : n-1for j = 2 : n-1if i == jmatrix(i-1,j-1) = (X(i+1)-X(i-1))/3;elseif i-j == 1matrix(i-1,j-1) = (X(i+1)-X(i))/6;elseif i- j == -1matrix(i-1,j-1) = (X(i+1)-X(i))/6;endendendb=zeros(9,1);for i=1:9  b(i) = (Y(i+2)-Y(i+1)) / (X(i+2)-X(i+1)) - (Y(i+1)-Y(i)) / (X(i+1)-X(i));endans = pinv(matrix) * b% plootting partx = -1: 0.002: 1;y=spline(X,Y,x);figure(1)plot(X,Y,"o",x,y)