L = 13N = 87h = L / Nn = [0 : 1 : N/2]k = 2*pi / L*nkh = 2*pi / N*nkh_second_order = sin(kh)kh_fourth_order = (-sin(4*pi*n/N)+8*sin(2*pi*n/N))/6kh_Pade_scheme = 3*sin(kh)./(2+cos(kh))% plotting partkh_true = khplot(kh,kh_second_order)hold onplot(kh, kh_true)plot(kh, kh_fourth_order)plot(kh, kh_Pade_scheme)hold offxlabel('kh');ylabel('kâ€²');legend('2nd order central difference', '4th order central difference','4th order Pade scheme', 'kh');print("-djpeg",["B_2",'.jpg'])