clearclch1=linspace(0, pi, 4+1)h2=linspace(0, pi, 8+1)h3=linspace(0, pi, 16+1)h4=linspace(0, pi, 32+1)g1=g2=g3=g4=0;for i=1:2:3  a = (pi/4)^3/24 * sin(h1(i))  b = (pi/4)^3/12 * sin(h1(i+1))  c = (pi/4)^3/24 * sin(h1(i+2))  d = 1/2 * (pi/4) * (sin(h1(i)) + 2*sin(h1(i+1)) + sin(h1(i+2)))  g1 = g1 + a + b + c + dendforfor i=1:2:7  a = (pi/8)^3/24 * sin(h2(i))  b = (pi/8)^3/12 * sin(h2(i+1))  c = (pi/8)^3/24 * sin(h2(i+2))  d = 1/2 * (pi/8) * (sin(h2(i)) + 2*sin(h2(i+1)) + sin(h2(i+2)))  g2=g2 + a + b + c + dendforfor i=1:2:15  a = (pi/16)^3/24 * sin(h3(i))  b = (pi/16)^3/12 * sin(h3(i+1))  c = (pi/16)^3/24 * sin(h3(i+2))  d = 1/2 * (pi/16) * (sin(h3(i)) + 2*sin(h3(i+1)) + sin(h3(i+2)))  g3=g3 + a + b + c + dendforfor i=1:2:31  a = (pi/32)^3/24*sin(h4(i))  b = (pi/32)^3/12*sin(h4(i+1))  c = (pi/32)^3/24*sin(h4(i+2))  d = 1/2 * (pi/32) * (sin(h4(i)) + 2*sin(h4(i+1)) + sin(h4(i+2)))  g4=g4 + a + b + c + dendforans1 = abs(g1-2)ans2 = abs(g2-2)ans3 = abs(g3-2)ans4 = abs(g4-2)error1=log10(abs(g1-2))error2=log10(abs(g2-2))error3=log10(abs(g3-2))error4=log10(abs(g4-2))x=[pi/4 pi/8 pi/16 pi/32];y=[error1 error2 error3 error4];hold onplot(log10(x),y,'-r');plot(log10(x),y,"o");hold offylabel("log(error)")xlabel("log(grid spacing)");print("-djpeg",["C2",'.jpg'])