clear allclcfunction f1=f(x);  f1=sin(2*x)+0.1*sin(15*x);endfunctionfunction f1=g(x);  f1=sin(2*x)+0.1*cos(15*x);endfunctionfunction re=remove_boundry_s(x)  L=size(x)(2);  re=x(1,1:L-1);endfunctionaxisx=linspace(0,2*pi,1387);n32=linspace(0,2*pi,33);x32=remove_boundry_s(n32);yf=f(x32);yg=g(x32);fk=fft(yf);gk=fft(yg);fkAsc=[fk,fk](1,17:48);gkAsc=[gk,gk](1,17:48);Hk = zeros(1,32);for k = -16:15for m = -16:15if(k-m>=-16 && k-m <= 15)Hk(k+17) = Hk(k+17)+fkAsc(m+17)*gkAsc(k-m+17);endendend