n1=1;x1=0:n1:15;euler=backeuler=trap=rknd=rkth=[]; euler(1)=backeuler(1)=trap(1)=rknd(1)=rkth(1)=4;%%%%%Eulerfor i=2:length(x1)euler(i)=euler(i-1).*(1-n1*(2+0.01*(x1(i)).^2));end%%%%backward Eulerfor i=1:(length(x1)-1)backeuler(i+1)=backeuler(i).*(1/(1+n1*(2+0.01*(x1(i)).^2)));end%%%%trapezoidal for i=2:length(x1)trap(i)=trap(i-1).*(1- n1*(2+0.01*(x1(i)).^2)/2)./(1+n1*(2+0.01*(x1(i)).^2)/2);end%%%second RKfor i=2:length(x1)rknd(i)=rknd(i-1).*(1-n1*(2+0.01*(x1(i)).^2)- (n1*(2+0.01*(x1(i)).^2))^2/2);end%%%forth RKfor i=2:length(x1)rkth(i)=rkth(i-1).*(1+(-n1*(2+0.01*(x1(i)).^2))+(- n1*(2+0.01*(x1(i)).^2)).^2/2+(-n1*(2+0.01*(x1(i)).^2)).^3/6+(- n1*(2+0.01*(x1(i)).^2)).^4/24);endx=linspace(0,15);y=4*(exp(-2*x-(0.01/3)*x.^3)); plot(x1,euler,'x',x1,backeuler,'@',x1,trap,'+',x1,rknd,'o',x1,rkth,'*',x,y);legend('Euler','backward Euler','trapezoidal','second-order Runge- Kutta','fourth-order Runge-Kutta','exact');xlabel("x");ylabel("y");legend('Location', "southeast")